mapper.insert:
INSERT INTO `restaurant_menu`
SET category_id = #{categoryId}
, menu_name = #{menuName}

    , menu_pic = #{menuPic}

, price = #{price}

    , details = #{details}

, available = #{available}

mapper.select:
SELECT menu_name AS menuName, price, details, available
FROM restaurant_menu
WHERE category_id = #{categoryId};
